%Adding blur using Image pkgimh = imread('panda.JPG');hsize = 31;sigma = 5 ; pkg load imageh = fspecial('gaussian' , hsize, sigma);surf(h);imagesc(h);outim = imfilter(imh, h)imshow(outim)%ALSOfor sigma = 1:3:10 h = fspecial('gaussian', hsize, sigma) out = imfilter(imh,h); imshow(out); pauseend% Apply a Gaussian filter to remove noiseimg = imread('saturn.jpeg');imshow(img);% TODO: Add noise to the imagenoisesigma = 20;noise = randn(size(img)) .* noisesigma;noise_img = img + noise;imshow(noise_img);% TODO: Now apply a Gaussian filter to smooth out the noisefs = 11;fsig = 4;pkg load image;fil = fspecial('gaussian',fs,fsig);% Note: You may need to pkg load image;smooth = imfilter(noise_img, fil);imshow(smooth)