%read image img = imread('panda.jpg');imshow(img);% TODO: Now apply a Gaussian filter to smooth out the noisefs = 21;fsig = 4;pkg load image;fil = fspecial('gaussian',fs,fsig);% applying various filterssmooth = imfilter(img, fil, 'symmetric');smooth = imfilter(img, fil, 'replicate');imshow(smooth)%add salt and pepper noisenoisey_img = imnoise(img, 'salt & pepper', 0.02);imshow(noisey_img)%apply median filter median_filtered = medfilt2(noisey_img);imshow(median_filtered)